import { useState, useEffect } from 'react';
import './App.css';
import Hero from './components/hero.jsx';

const images = [
  '/assets/hero1.jpg',
  '/assets/hero2.jpg',
  '/assets/hero3.jpg',
];

function App() {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrent((prev) => (prev + 1) % images.length);
    }, 4000); // Change every 4 seconds

    return () => clearInterval(timer);
  }, []);

  return (
    <div
      className="min-h-screen bg-cover bg-center transition-all duration-1000 ease-in-out text-white flex flex-col"
      style={{ backgroundImage: `url(${images[current]})` }}
    >
      {/* Navbar */}
      <nav className="flex justify-between items-center px-8 py-6 bg-black bg-opacity-30">
        <h1 className="text-2xl font-bold">Sign Galaxy</h1>
        <ul className="flex space-x-6 text-lg font-medium">
          <li className="hover:text-yellow-300 transition duration-300 cursor-pointer">Home</li>
          <li className="hover:text-yellow-300 transition duration-300 cursor-pointer">About Us</li>
          <li className="hover:text-yellow-300 transition duration-300 cursor-pointer">Services</li>
          <li className="hover:text-yellow-300 transition duration-300 cursor-pointer">Contact</li>
        </ul>
      </nav>

      {/* Hero Section */}
      <div className="flex-1 flex items-center px-10">
        <div className="bg-black bg-opacity-50 p-6 rounded-xl max-w-xl">
          <h2 className="text-4xl font-semibold mb-4">Design that Speaks.</h2>
          <p className="text-lg text-gray-200 mb-6">
            We bring life to spaces â€“ Interior. Exterior. Signage. All in one.
          </p>
          <div className="flex space-x-4">
            <button className="px-6 py-2 bg-yellow-400 text-black font-semibold rounded-xl hover:bg-yellow-300 transition">
              Explore Services
            </button>
            <button className="px-6 py-2 border border-white rounded-xl hover:bg-white hover:text-black transition">
              Contact Us
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
