import { useState, useEffect } from 'react';
import './App.css';

const images = [
  '/assets/hero1.jpg',
  '/assets/hero2.jpg',
  '/assets/hero3.jpg',
];


function App() {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrent((prev) => (prev + 1) % images.length);
    }, 4000); // every 4 seconds

    return () => clearInterval(timer);
  }, []);

  return (
    <div
      className="min-h-screen bg-cover bg-center transition-opacity duration-[2000ms] ease-in-out text-white flex flex-col"
      style={{
        backgroundImage: `url(${images[current]})`,
        transition: 'background-image 1.5s ease-in-out'
      }}
    >
      {/* Navbar */}
      <nav className="flex justify-between items-center px-8 py-6 bg-black bg-opacity-30">
        <h1 className="text-2xl font-bold">Sign Galaxy</h1>
        <ul className="flex space-x-6 text-lg font-medium">
          <li className="hover:text-yellow-300 cursor-pointer">Home</li>
          <li className="hover:text-yellow-300 cursor-pointer">About Us</li>
          <li className="hover:text-yellow-300 cursor-pointer">Services</li>
          <li className="hover:text-yellow-300 cursor-pointer">Contact</li>
        </ul>
      </nav>

      {/* Hero Quote */}
      <div className="flex-1 flex items-center justify-center px-6 text-center">
        <div>
          <h2 className="text-5xl md:text-6xl font-bold drop-shadow-md">
            Elevate Spaces with Sign Galaxy
          </h2>
          <p className="mt-4 text-xl text-gray-100">
            Crafting bold impressions through signs, interiors, and exteriors.
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
