import { useState, useEffect } from 'react';
import './App.css';

const images = [
  '/images/hero1.jpg',
  '/images/hero2.jpg',
  '/images/hero3.jpg',
];

function App() {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrent((prev) => (prev + 1) % images.length);
    }, 4000);
    return () => clearInterval(timer);
  }, []);

  return (
    <div className="relative h-screen w-full overflow-hidden text-white">
      {/* Background Layer */}
      {images.map((img, index) => (
        <div
          key={index}
          className={`absolute top-0 left-0 h-full w-full bg-cover bg-center transition-opacity duration-1000 ease-in-out ${
            current === index ? 'opacity-100 z-10' : 'opacity-0 z-0'
          }`}
          style={{ backgroundImage: `url(${img})` }}
        />
      ))}

      {/* Overlay (optional for dark fade) */}
      <div className="absolute top-0 left-0 h-full w-full bg-black bg-opacity-30 z-20" />

      {/* Navbar */}
      <nav className="relative z-30 flex justify-between items-center px-8 py-6 bg-black bg-opacity-30">
        <h1 className="text-2xl font-bold">Sign Galaxy</h1>
        <ul className="flex space-x-6 text-lg font-medium">
          <li className="hover:text-yellow-300 transition duration-300 cursor-pointer">Home</li>
          <li className="hover:text-yellow-300 transition duration-300 cursor-pointer">About Us</li>
          <li className="hover:text-yellow-300 transition duration-300 cursor-pointer">Services</li>
          <li className="hover:text-yellow-300 transition duration-300 cursor-pointer">Contact</li>
        </ul>
      </nav>

      {/* Hero Quote */}
      <div className="relative z-30 flex items-center justify-center h-screen px-6 text-center -translate-y-10">
        <div>
          <h2 className="text-5xl md:text-6xl font-bold drop-shadow-md transition-all duration-1000 ease-in-out">
            Elevate Spaces with Sign Galaxy
          </h2>
          <p className="mt-4 text-xl text-gray-100">
            Crafting bold impressions through signs, interiors, and exteriors.
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
{/* About Us Section */}
<section className="bg-white text-gray-800 px-8 py-20" id="about">
  <div className="max-w-6xl mx-auto text-center">
    <h2 className="text-4xl font-bold mb-6">About Us</h2>
    <p className="text-lg leading-relaxed max-w-3xl mx-auto">
      At <span className="font-semibold text-yellow-600">Sign Galaxy</span>, we transform spaces into experiences.
      With a perfect blend of creativity, innovation, and craftsmanship, we offer end-to-end solutions in 
      signage, interiors, and exteriors. Whether it's a sleek office makeover or impactful branding signage, 
      we make your vision speak boldly.
    </p>
    <div className="mt-10 flex flex-wrap justify-center gap-6">
      <div className="w-64 bg-yellow-100 rounded-xl shadow p-6 hover:shadow-lg transition duration-300">
        <h3 className="text-xl font-semibold text-yellow-700 mb-2">Creative Designs</h3>
        <p>Modern and visually stunning ideas crafted just for your brand.</p>
      </div>
      <div className="w-64 bg-yellow-100 rounded-xl shadow p-6 hover:shadow-lg transition duration-300">
        <h3 className="text-xl font-semibold text-yellow-700 mb-2">End-to-End Execution</h3>
        <p>From concept to completion — we manage it all under one roof.</p>
      </div>
      <div className="w-64 bg-yellow-100 rounded-xl shadow p-6 hover:shadow-lg transition duration-300">
        <h3 className="text-xl font-semibold text-yellow-700 mb-2">Customer Satisfaction</h3>
        <p>We build long-lasting relationships through exceptional service.</p>
      </div>
    </div>
  </div>
</section>
