import { useState, useEffect } from 'react';
import './App.css';

const images = [
  '/images/hero1.jpg',
  '/images/hero2.jpg',
  '/images/hero3.jpg',
];



function App() {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrent((prev) => (prev + 1) % images.length);
    }, 3000); // every 4 seconds

    return () => clearInterval(timer);
  }, []);

 return (
  <div className="relative min-h-screen text-white overflow-hidden">
    {/* Background Images with Transition */}
    {images.map((img, index) => (
      <div
        key={index}
        className={`absolute inset-0 bg-cover bg-center transition-opacity duration-1000 ease-in-out transform ${
          index === current ? 'opacity-100 scale-105 z-10' : 'opacity-0 scale-100'
        }`}
        style={{ backgroundImage: `url(${img})` }}
      />
    ))}

    {/* Navbar */}
    <nav className="relative z-20 flex justify-between items-center px-8 py-6 bg-black bg-opacity-40">
      <h1 className="text-2xl font-bold">Sign Galaxy</h1>
      <ul className="flex space-x-6 text-lg font-medium">
        <li className="hover:text-yellow-300 cursor-pointer">Home</li>
        <li className="hover:text-yellow-300 cursor-pointer">About Us</li>
        <li className="hover:text-yellow-300 cursor-pointer">Services</li>
        <li className="hover:text-yellow-300 cursor-pointer">Contact</li>
      </ul>
    </nav>

    {/* Hero Quote (Centered) */}
<div className="relative z-20 flex items-center justify-center h-screen px- text-center">
      <div>
        <h2 className="text-5xl md:text-6xl font-bold drop-shadow-md transition-all duration-1000 ease-in-out">
          Elevate Spaces with Sign Galaxy
        </h2>
        <p className="mt-4 text-xl text-gray-100">
          Crafting bold impressions through signs, interiors, and exteriors.
        </p>
      </div>
    </div>
  </div>
);

}

export default App;
