// src/components/Services/ServicesMain.jsx
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';

const services = [
  {
    title: 'Interiors',
    image: '/images/interiors/sample.jpg', // replace with your actual image
    path: '/services/interior',
    description: 'Beautiful spaces crafted with detail and purpose.',
  },
  {
    title: 'Exteriors & Facade',
    image: '/images/exteriors/sample.jpg', // replace with your actual image
    path: '/services/exterior',
    description: 'Modern exteriors and façade work for every structure.',
  },
  {
    title: 'Signage',
    image: '/images/signage/sample.jpg', // replace with your actual image
    path: '/services/signage',
    description: 'Striking signage solutions that speak loud and clear.',
  },
];

const ServicesMain = () => {
  const navigate = useNavigate();

  return (
    <div className="bg-gradient-to-b from-black via-gray-900 to-black min-h-screen py-20 px-6 text-white">
      <div className="max-w-6xl mx-auto text-center">
        <h2 className="text-4xl font-bold text-yellow-300 mb-12">Our Services</h2>

        <div className="grid gap-10 md:grid-cols-3">
          {services.map((service, index) => (
            <motion.div
              key={index}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.98 }}
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.6, delay: index * 0.2 }}
              className="cursor-pointer bg-white/10 p-6 rounded-2xl shadow-lg backdrop-blur-md hover:shadow-yellow-300"
              onClick={() => navigate(service.path)}
            >
              <img
                src={service.image}
                alt={service.title}
                className="w-full h-48 object-cover rounded-xl mb-4"
              />
              <h3 className="text-2xl font-semibold text-yellow-300">{service.title}</h3>
              <p className="text-sm mt-2 text-gray-200">{service.description}</p>
            </motion.div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ServicesMain;
