// Interior.jsx
import { useEffect } from 'react';
import { motion } from 'framer-motion';
<button 
  onClick={() => window.history.back()} 
  className="mt-4 ml-4 px-4 py-2 bg-gray-200 hover:bg-gray-300 text-gray-800 rounded transition"
>
  ‚Üê Back to Services
</button>

const interiorCategories = [
  {
    
    title: 'Kitchens',
    folder: 'kitchen',
    images: [1, 2, 3, 4, 5],
  },
  {
    title: 'Office Interiors',
    folder: 'office',
    images: [1, 2, 3, 4, 5],
  },
  {
    title: 'Living Areas / False Ceilings',
    folder: 'false',
    images: [1, 2, 3, 5],
  },
  {
    title: 'Bedrooms',
    folder: 'bedroom',
    images: [ 2, 3, 4, 5],
  },
];

const fadeInUp = {
  hidden: { opacity: 0, y: 40 },
  visible: (i) => ({
    opacity: 1,
    y: 0,
    transition: { delay: i * 0.2, duration: 0.8, ease: 'easeInOut' },
  }),
};

const Interior = () => {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <div className="bg-black min-h-screen text-white pt-28 pb-16 px-6">
      <h2 className="text-4xl font-bold text-yellow-300 text-center mb-10">
        Interior Design Works
      </h2>

      {interiorCategories.map((category, catIndex) => (
        <section key={catIndex} className="mb-20">
          <motion.h3
            className="text-2xl font-semibold mb-8 text-center text-white"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            {category.title}
          </motion.h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8 max-w-6xl mx-auto">
            {category.images.map((num, imgIndex) => (
              <motion.div
                key={imgIndex}
                custom={imgIndex}
                initial="hidden"
                whileInView="visible"
                viewport={{ once: true }}
                variants={fadeInUp}
                className="overflow-hidden rounded-2xl shadow-2xl bg-gray-900 hover:scale-105 transform transition duration-700"
              >
                <img
                  src={`/images/int${category.folder}${num}.jpg`}
                  alt={`${category.title} ${num}`}
                  className="object-cover h-64 w-full rounded-2xl"
                />
              </motion.div>
            ))}
          </div>
        </section>
      ))}
    </div>
  );
};

export default Interior;
