// Interior.jsx
import { useEffect } from 'react';
import { motion } from 'framer-motion';

const interiorCategories = [
  {
    title: 'Bedrooms',
    folder: 'bedroom',
    images: [1, 2, 3, 4, 5],
  },
  {
    title: 'Kitchens',
    folder: 'kitchen',
    images: [1, 2, 3, 4, 5],
  },
  {
    title: 'Living Areas / False Ceilings',
    folder: 'false',
    images: [1, 2, 3, 4, 5],
  },
  {
    title: 'Office Interiors',
    folder: 'office',
    images: [1, 2, 3, 4, 5],
  },
];

const fadeInUp = {
  hidden: { opacity: 0, y: 30 },
  visible: (i) => ({
    opacity: 1,
    y: 0,
    transition: { delay: i * 0.15, duration: 0.6, ease: 'easeOut' },
  }),
};

const Interior = () => {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <div className="bg-black min-h-screen text-white pt-28 pb-16 px-6">
      <h2 className="text-4xl font-bold text-yellow-300 text-center mb-10">
        Interior Design Works
      </h2>

      {interiorCategories.map((category, catIndex) => (
        <section key={catIndex} className="mb-16">
          <h3 className="text-2xl font-semibold mb-6 text-center text-white">
            {category.title}
          </h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 max-w-6xl mx-auto">
            {category.images.map((num, imgIndex) => (
              <motion.img
                key={imgIndex}
                custom={imgIndex}
                initial="hidden"
                whileInView="visible"
                viewport={{ once: true }}
                variants={fadeInUp}
                src={`/images/int${category.folder}${num}.jpg`}
                alt={`${category.title} ${num}`}
                className="rounded-xl shadow-lg hover:scale-105 transition-transform duration-300 object-cover h-64 w-full"
              />
            ))}
          </div>
        </section>
      ))}
    </div>
  );
};

export default Interior;
